mapper.select:
select ${column} as "column_name"
from ${table}
where
	time = #{time}
and nename = '合计'

select ${column} as "column_name", DATE_FORMAT(TIME,

	'%m-%d'


	'%m-%d %H'


	'%H:%i'

) TIME
from ${table}
where
	nename = #{neName}
and
 	time between #{firstTime} and #{lastTime}
order by time;
